import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button, Dropdown } from 'react-bootstrap';

const HomePage = () => {
  const { isAuthenticated, loginWithRedirect, logout, user } = useAuth0();
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [classrooms, setClassrooms] = useState([]);
  const [isLoading, setIsLoading] = useState(true); // Added loading state
  const [error, setError] = useState('');

  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };

  useEffect(() => {
    const fetchClassrooms = async () => {
      try {
        const response = await fetch('/classrooms');
        if (response.ok) {
          const data = await response.json();
          setClassrooms(data);
        } else {
          throw new Error('Failed to fetch classrooms');
        }
      } catch (error) {
        console.error('Error fetching classrooms:', error);
        setError('Failed to fetch classrooms. Please try again later.');
      } finally {
        setIsLoading(false); // Update loading state when fetching is done
      }
    };

    if (isAuthenticated) {
      fetchClassrooms();
    }
  }, [isAuthenticated]);

  const handleCreateClassroom = async () => {
    try {
      const response = await fetch('/classrooms', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title: 'New Classroom', description: 'Description of the new classroom' }),
      });
      if (response.ok) {
        const data = await response.json();
        // Add the newly created classroom to the list
        setClassrooms([...classrooms, data]);
        setError(''); // Clear any previous error
      } else {
        throw new Error('Failed to create classroom');
      }
    } catch (error) {
      console.error('Error creating classroom:', error);
      setError('Failed to create classroom. Please try again later.');
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="container-fluid">
        <h1 style={{ textAlign: 'center' }}>Welcome to EzQuiz</h1>
        <div>
          <Button onClick={loginWithRedirect}>Log In</Button>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      <h1 style={{ textAlign: 'center' }}>Welcome to EzQuiz</h1>
      <div>
        <h2>My Classrooms</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        <ul>
          {classrooms.map((classroom, index) => (
            <li key={index}>{classroom.title}</li>
          ))}
        </ul>
        <Button onClick={handleCreateClassroom}>Create Classroom</Button>
      </div>
    </div>
  );
}

export default HomePage;
