import React, { useState } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';

function QuizGenerator() {
    const [inputText, setInputText] = useState('');
    const [questions, setQuestions] = useState([]);
    const [showWidget, setShowWidget] = useState(false); // State to control widget visibility

    const handleCreateQuiz = async () => {
        try {
            const response = await axios.post('http://localhost:5000/generate_questions', { input_text: inputText });
            setQuestions(response.data.questions.map(question => ({ ...question, selectedOption: '', isCorrect: null })));
            setShowWidget(true); // Show the widget after generating questions
        } catch (error) {
            console.error('Error generating quiz:', error);
        }
    };

    const handleInputChange = (event) => {
        setInputText(event.target.value);
    };

    const handleOptionClick = (questionIndex, option) => {
        const updatedQuestions = [...questions];
        const question = updatedQuestions[questionIndex];
        if (question.selectedOption === '') {
            question.selectedOption = option;
            question.isCorrect = option === question.answer;
        }
        setQuestions(updatedQuestions);
    };

    const optionStyle = {
        cursor: 'pointer',
        padding: '10px',
        marginBottom: '5px',
        borderRadius: '5px',
        transition: 'background-color 0.3s ease',
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1 style={{ fontSize: '2rem', marginBottom: '20px' }}>Quiz Generator</h1>
            <textarea className="form-control"
                style={{
                    width: '80%',
                    margin: '0 auto', // Align center horizontally
                    marginBottom: '30px',
                    padding: '10px',
                    fontSize: '1rem',
                }}
                rows="4"
                value={inputText}
                onChange={handleInputChange}
            ></textarea>
            <Button
                style={{
                    backgroundColor: '#007bff',
                    display: 'block',
                    margin: '0 auto',
                    width: '45%',
                    color: 'white',
                    border: 'none',
                    padding: '10px 20px',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    transition: 'background-color 0.3s ease',
                }}
                onClick={handleCreateQuiz}
            >
                Create Quiz
            </Button>
            {showWidget && (
                <div className="max-w-sm mx-auto p-4">
                    <div className="bg-white p-4 rounded-lg shadow-md">
                        <h2 className="font-bold text-lg">2. For Questions 2-5 use below table</h2>
                        <div className="mt-4 mb-2 p-2 bg-zinc-50 rounded">
                            <p className="text-sm">For questions 2-5, consider the following small undefinedbase of four transactions. The minimum support is 60% and the minimum confidence is 80%.</p>
                           
                        </div>
                        <div className="mt-4">
                            <p>The 2-itemsets that satisfy the support criteria are:</p>
                            <div className="flex flex-col">
                                <label className="inline-flex items-center mt-3">
                                    <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset" value="A"/><span className="ml-2 text-zinc-700"></span>
                                </label>
                                <label className="inline-flex items-center mt-3">
                                    <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset" value="B"/><span className="ml-2 text-zinc-700">B. {AB}, {BD}, {AD}</span>
                                </label>
                                <label className="inline-flex items-center mt-3">
                                    <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset" value="C"/><span className="ml-2 text-zinc-700">C. {AE}, {BC}</span>
                                </label>
                                <label className="inline-flex items-center mt-3">
                                    <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset" value="D"/><span className="ml-2 text-zinc-700">D. {BC}</span>
                                </label>
                            </div>
                        </div>
                        <div className="bg-white p-4 rounded-lg shadow-md mt-4">
                            <h2 className="font-bold text-lg">3. *</h2>
                            <div className="mt-4 p-2 bg-zinc-50 rounded">
                                <p>The 3-itemsets that satisfy the support criteria are:</p>
                                <div className="flex flex-col">
                                    <label className="inline-flex items-center mt-3">
                                        <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset3" value="A"/><span className="ml-2 text-zinc-700">A. {ABC}, {ABE}, {BCD}, {ACD}</span>
                                    </label>
                                    <label className="inline-flex items-center mt-3">
                                        <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset3" value="B"/><span className="ml-2 text-zinc-700">B. {ABE}, {BCD}, {ACD}</span>
                                    </label>
                                    <label className="inline-flex items-center mt-3">
                                        <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset3" value="C"/><span className="ml-2 text-zinc-700">C. {ABE}, {BCD}</span>
                                    </label>
                                    <label className="inline-flex items-center mt-3">
                                        <input type="radio" className="form-radio h-5 w-5 text-zinc-600" name="itemset3" value="D"/><span className="ml-2 text-zinc-700">D. {ABD}</span>
                                    </label>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default QuizGenerator;
