import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import 'bootstrap/dist/js/bootstrap.bundle.min';
import { Card, Button } from 'react-bootstrap';
// import './ClassroomCards.css'; // Import the external CSS file

const ClassroomCards = () => {
    const [classrooms, setClassrooms] = useState([]);

    const fetchClassrooms = async () => {
        try {
            const response = await axios.get('http://localhost:8000/classroom');
            setClassrooms(response.data.classrooms);
        } catch (error) {
            console.error('Error fetching classrooms:', error);
        }
    };

    useEffect(() => {
        fetchClassrooms();
    }, []);

    return (
        <>
            <div className="min-h-screen bg-white dark:bg-zinc-800 p-6">
                <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
                    {classrooms.length > 0 ? (
                        classrooms.map(classroom => (
                            <div key={classroom._id} className="classroom-card">
                                <div className="classroom-header">
                                    <h2 className="classroom-title">{classroom.title}</h2>
                                    <p className="classroom-description">{classroom.decription}</p>
                                </div>
                                <div className="classroom-content">
                                    <div className="teacher-info">
                                        <img className="teacher-image" src="https://placehold.co/64x64" alt="Teacher Image" />
                                        <div className="teacher-details">
                                            <h3 className="teacher-name">{classroom.description}</h3>
                                        </div>
                                    </div>
                                    <div className="enroll-button">
                                        <button className="enroll-btn">
                                            Enroll
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p className="no-classroom-msg">No classrooms available</p>
                    )}
                </div>
            </div>
        </>
    );
};

export default ClassroomCards;


const styles = `
.classroom-container {
    min-height: 100vh;
    background-color: #fff;
    padding: 1.5rem;
}

.classroom-card {
    margin-bottom: 1.5rem;
}

.classroom-header {
    background-color: #3182ce;
    padding: 1rem;
}

.classroom-header h2 {
    color: #fff;
    font-size: 1.25rem;
    font-weight: bold;
    margin-bottom: 0.5rem;
}

.classroom-header p {
    color: rgba(255, 255, 255, 0.9);
}

.classroom-body {
    padding: 1rem;
    border-top: 1px solid #e2e8f0;
}

.teacher-info {
    display: flex;
    align-items: center;
    margin-bottom: 1rem;
}

.teacher-info img {
    width: 64px;
    height: 64px;
    border-radius: 50%;
    margin-right: 1rem;
}

.teacher-info h3 {
    font-weight: bold;
}

.enroll-button {
    text-align: right;
}

.enroll-btn {
    color: #3182ce;
    background-color: transparent;
    border: none;
    cursor: pointer;
    font-weight: bold;
}

.no-classrooms-msg {
    color: red;
    font-style: italic;
    text-align: center;
}


`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
