import React, { useState } from 'react';
import QuizList from './QuizList';
import EditQuiz from './EditQuiz';

const ClassroomView = ({ classroom, user }) => {
  const [selectedQuiz, setSelectedQuiz] = useState(null);

  const handleQuizSelect = quiz => {
    setSelectedQuiz(quiz);
  };

  return (
    <div className="classroom-view">
      <h2>{classroom.title}</h2>
      <p>{classroom.description}</p>
      {/* Display number of enrolled students */}
      <p>Enrolled Students: {classroom.enrolledStudents.length}</p>
      
      {/* Display quizzes for teacher */}
      {user.role === 'teacher' && (
        <>
          <h3>Quizzes</h3>
          <QuizList quizzes={classroom.quizzes} onSelectQuiz={handleQuizSelect} />
          {/* Edit quiz modal */}
          {selectedQuiz && <EditQuiz quiz={selectedQuiz} />}
        </>
      )}

      {/* Display quizzes for students */}
      {user.role === 'student' && (
        <>
          <h3>Assigned Quizzes</h3>
          <QuizList quizzes={classroom.assignedQuizzes} />
        </>
      )}
    </div>
  );
};

export default ClassroomView;
