import React, { useState, useEffect, useRef } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button } from 'react-bootstrap';
import HomePage from '...'// Import the HomePage component
import axios from 'axios';

const Classroom = () => {
  const { isAuthenticated, loginWithRedirect, logout, user } = useAuth0();
  const [showDropdown, setShowDropdown] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [classrooms, setClassrooms] = useState([]);

  const dropdownRef = useRef(null);
  const userDropdownRef = useRef(null);
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };
  useEffect(() => {
    const fetchClassrooms = async () => {
      try {
        const response = await axios.get('http://localhost:8000/classrooms');
        setClassrooms(response.data);
      } catch (error) {
        console.error('Error fetching classrooms:', error);
      }
    };

    fetchClassrooms();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
      if (userDropdownRef.current && !userDropdownRef.current.contains(event.target)) {
        setShowUserDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);
  return (
    <div>
      <nav className="navbar">
        <div className="container" style={{overflow:'visible'}}>
          <div className="navbar-brand">
            {isAuthenticated ? (
              <a href="/" style={{ textDecoration: 'none' }} onClick={(e) => { e.preventDefault(); }}>
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name">EzOuiz</span>
              </a>
            ) : (
              <a href="/" style={{ textDecoration: 'none' }}>
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name">EzOuiz</span>
              </a>
            )}
          </div>
          <div className="navbar-icons">
            {isAuthenticated && (
              <div className="dropdown" onClick={toggleDropdown} ref={dropdownRef}>
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#000000" className="bi bi-plus" viewBox="0 0 24 24" style={{marginLeft:'20'}}>
                  <path d="M11 5h2v14h-2z" />
                  <path d="M5 11h14v2H5z" />
                </svg> 

                <div className={`dropdown-menu ${showDropdown ? 'show' : ''}`}>
                  <div className="dropdown-item" onClick={() => window.location.href = '/Create'}>Create Classroom</div>
                  <div className="dropdown-item">Join Classroom</div>
                </div>
              </div>
            )}
            {isAuthenticated && (
              <div className="dropdown" onClick={toggleUserDropdown} style={{ position: 'relative', top: 0, right: 0 , marginLeft:"20px"}} ref={userDropdownRef}>
                <img src={user.picture} alt={user.name} style={{ width: 35, height: 35, borderRadius: 50, marginLeft:'20px', cursor: 'pointer'  }} />
                <div className={`dropdown-menu ${showUserDropdown ? 'show' : ''}`}>
                  <div className="dropdown-item">Profile</div>
                  <div className="dropdown-item">Settings</div>
                  <div className="dropdown-item" onClick={() => logout({ returnTo: window.location.origin })}>Logout</div>
                </div>
              </div>
            )}
            {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
          </div>
        </div>
      </nav>
      <div className='container-fluid'>
        {isAuthenticated && (
          <HomePage /> 
                  )}
      </div>
    </div>
  );
}

export default Classroom;
