import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Classroom = () => {
    const [classrooms, setClassrooms] = useState([]);

    const fetchClassrooms = async () => {
        try {
            const response = await axios.get('http://localhost:8000/classrooms');
            setClassrooms(response.data);
        } catch (error) {
            console.error('Error fetching classrooms:', error);
        }
    };

    useEffect(() => {
        fetchClassrooms();
    }, []);

    const handleCreateClassroom = async () => {
        try {
            const response = await axios.post('http://localhost:8000/classrooms', {
                title: 'New Classroom', // Example title
                description: 'Example description' // Example description
            });
            if (response.status === 201) {
                // Fetch the updated list of classrooms after creating a new one
                fetchClassrooms();
            }
        } catch (error) {
            console.error('Error creating classroom:', error);
        }
    };

    return (
        <div>
            <h1>Welcome to the EzQuiz</h1>
            <button onClick={handleCreateClassroom}>Create Classroom</button>
            {classrooms.map(classroom => (
                <div key={classroom._id}>
                    <h2>{classroom.title}</h2>
                    <p>{classroom.description}</p>
                </div>
            ))}
        </div>
    );
};

export default Classroom;
