import React, { useState } from 'react';
import axios from 'axios';

const CreateClassroom = () => {
  const [className, setClassName] = useState('');
  const [classDescription, setClassDescription] = useState('');
  const [error, setError] = useState('');

  const handleCreateClassroom = async () => {
    try {
      const response = await axios.post('http://localhost:5000/classrooms', {
        className,
        classDescription
      });
      console.log(response.data);
      // Handle success, e.g., redirect to homepage
    } catch (error) {
      console.error('Error creating classroom:', error);
      setError('Failed to create classroom');
    }
  };

  const handleChange = (e) => {
    setError('');
    const { id, value } = e.target;
    if (id === 'className') {
      setClassName(value);
    } else if (id === 'classDescription') {
      setClassDescription(value);
    }
  };

  return (
    <div style={{ maxWidth: '500px', margin: '0 auto', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', backgroundColor: '#f9f9f9' }}>
      <h1>Create Classroom</h1>
      <form>
        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="className" style={{ display: 'block', fontWeight: 'bold' }}>Class Name:</label>
          <input type="text" id="className" value={className} onChange={handleChange} style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '5px' }} />
        </div>
        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="classDescription" style={{ display: 'block', fontWeight: 'bold' }}>Class Description:</label>
          <textarea id="classDescription" value={classDescription} onChange={handleChange} style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '5px' }}></textarea>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="button" onClick={handleCreateClassroom} style={{ backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '5px', padding: '10px 20px', cursor: 'pointer' }}>Create Classroom</button>
      </form>
    </div>
  );
}

export default CreateClassroom;
