import React from 'react';
import axios from 'axios';
import { useAuth0 } from '@auth0/auth0-react';

const ClassroomInterface = ({ classroom }) => {
  const { user } = useAuth0();

  // You can remove the fetchUserClassrooms function and the useEffect hook
  // Since you're now passing the classroom as a prop, there's no need to fetch it again

  return (
    <body>
      <div className="container mx-auto px-4">
        <div className="bg-blue-500 text-white p-4 rounded-lg shadow-md">
          <h1 className="text-3xl font-bold">{classroom.title}</h1>
          <p className="text-xl">{classroom.teacherName}</p>
        </div>

        <div className="grid grid-cols-3 gap-4 mt-4">
          <div className="col-span-1 bg-zinc-100 p-4 rounded-lg shadow-sm">
            <h2 className="font-semibold text-lg mb-2">Upcoming</h2>
            <p>Woohoo, no work due soon!</p>
            <button className="mt-4 bg-blue-600 text-white rounded py-2 px-4">View all</button>
          </div>

          <div className="col-span-2">
            {/* Assuming classroom materials are provided as an array in classroom.materials */}
            {classroom.materials.map((material, index) => (
              <div key={index} className="bg-white p-4 rounded-lg shadow-sm mb-4">
                <div className="flex items-center justify-between">
                  <h3 className="font-semibold text-lg">{material.author}</h3>
                  <p className="text-sm text-zinc-600">{material.date}</p>
                </div>
                <p>{material.content}</p>
                {material.documentLink && (
                  <a href={material.documentLink} className="text-blue-600">
                    Document Link
                  </a>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </body>
  );
}

export default ClassroomInterface;
