import React from 'react';
import ClassroomForm from './ClassroomForm';

const CreateClassroom = ({ onCreateClassroom }) => {
  const handleCreateClassroom = classroomData => {
    // Call the onCreateClassroom function passed from the parent component
    onCreateClassroom(classroomData);
  };

  const styles = {
    container: {
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f9f9f9',
      borderRadius: '8px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    },
    heading: {
      marginBottom: '20px',
      fontSize: '24px',
      color: '#333',
    },
    form: {
      padding: '20px',
      backgroundColor: '#fff',
      borderRadius: '8px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    },
    formGroup: {
      marginBottom: '20px',
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Create a New Classroom</h2>
      <div style={styles.form}>
        <div className="form-group" style={styles.formGroup}>
          <label htmlFor="className">Class Name</label>
          <input type="text" className="form-control" id="className" placeholder="Enter class name" />
        </div>
        <div className="form-group" style={styles.formGroup}>
          <label htmlFor="classDescription">Class Description</label>
          <textarea className="form-control" id="classDescription" rows="3" placeholder="Enter class description"></textarea>
        </div>
        <button type="submit" className="btn btn-primary">Create Classroom</button>
      </div>
    </div>
  );
};

export default CreateClassroom;
