import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button, Dropdown } from 'react-bootstrap'; // Import Dropdown component from react-bootstrap

const HomePage = () => {
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const [showDropdown, setShowDropdown] = useState(false); // State to control visibility of dropdown

  // Function to toggle visibility of dropdown
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  useEffect(() => {
    // Load FontAwesome CSS
    const link = document.createElement('link');
    link.href = "https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css";
    link.rel = "stylesheet";
    document.head.appendChild(link);

    return () => {
      // Cleanup on unmount
      document.head.removeChild(link);
    };
  }, []);

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="container">
          <div className="navbar-brand">
            <img src="/docs/5.3/assets/brand/bootstrap-logo.svg" alt="Bootstrap" width="30" height="24" />
            <span className="brand-name">Your Brand</span>
          </div>
          <div className="navbar-icons">
            {/* + Icon for toggling dropdown */}
            <Dropdown show={showDropdown} onClick={toggleDropdown}>
              <Dropdown.Toggle variant="link" id="dropdown-basic">
                <i className="fas fa-plus"></i>
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item href="#">Option 1</Dropdown.Item>
                <Dropdown.Item href="#">Option 2</Dropdown.Item>
                {/* Add more options as needed */}
              </Dropdown.Menu>
            </Dropdown>
          </div>
        </div>
      </nav>

      {/* Your existing content */}
      <div className='container-fluid'>
        <h1 style={{ textAlign: 'center' }}>Welcome to the EzQuiz </h1>
        {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
        {isAuthenticated && (
          <div>
            {/* Dashboard with options for enrolled classes, create class, etc. */}
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;

// CSS for navbar styles
const styles = `
/* Navbar styles */
.navbar {
  background-color: #007bff; /* Change the navbar background color */
  color: #fff;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
}

/* Navbar icons styles */
.navbar-icons {
  display: flex;
  align-items: center;
}

/* Icon button styles */
.icon-btn {
backgound-color:white;
  border: none;
  color: #ffff; /* Change the color of the icon */
  font-size: 18px;
  cursor: pointer;
  margin-left: 10px;
}

/* Media queries for responsiveness */
@media screen and (max-width: 768px) {
  /* Adjust styles for smaller screens */
  .navbar {
    flex-direction: column;
    align-items: flex-start;
  }
  
  .brand-name {
    margin-left: 0;
    margin-top: 10px;
  }
  i{
    color:white;
  }
  
  .navbar-icons {
    margin-top: 10px;
  }
}
`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
