// QuizGenerator.js

import React, { useState } from 'react';
import axios from 'axios';

function QuizGenerator() {
    const [inputText, setInputText] = useState('');
    const [questions, setQuestions] = useState([]);

    const handleCreateQuiz = async () => {
        try {
            const response = await axios.post('http://localhost:5000/generate_questions', { input_text: inputText });
            setQuestions(response.data.questions);
        } catch (error) {
            console.error('Error generating quiz:', error);
        }
    };

    const handleInputChange = (event) => {
        setInputText(event.target.value);
    };

    return (
        <div>
            <h1>Quiz Generator</h1>
            <textarea rows="4" cols="50" value={inputText} onChange={handleInputChange}></textarea>
            <button onClick={handleCreateQuiz}>Create Quiz</button>
            <div>
                {questions.map((question, index) => (
                    <div key={index}>
                        <h3>{question.question_statement}</h3>
                        <ul>
                            {question.options.map((option, optionIndex) => (
                                <li key={optionIndex}>
                                    <span style={{ color: option === question.answer ? 'green' : 'red' }}>
                                        {option}
                                    </span>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default QuizGenerator;
