import React, { useState } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';

function QuizGenerator() {
    const [inputText, setInputText] = useState('');
    const [questions, setQuestions] = useState([]);
    const [showWidget, setShowWidget] = useState(false); // State to control widget visibility

    const handleCreateQuiz = async () => {
        try {
            const response = await axios.post('http://localhost:5000/generate_questions', { input_text: inputText });
            setQuestions(response.data.questions.map(question => ({ ...question, selectedOption: '', isCorrect: null })));
            setShowWidget(false); // Hide the widget after generating questions
        } catch (error) {
            console.error('Error generating quiz:', error);
        }
    };

    const handleInputChange = (event) => {
        setInputText(event.target.value);
    };

    const handleOptionClick = (questionIndex, option) => {
        const updatedQuestions = [...questions];
        const question = updatedQuestions[questionIndex];
        if (question.selectedOption === '') {
            question.selectedOption = option;
            question.isCorrect = option === question.answer;
        }
        setQuestions(updatedQuestions);
    };

    const optionStyle = {
        cursor: 'pointer',
        padding: '10px',
        marginBottom: '5px',
        borderRadius: '5px',
        transition: 'background-color 0.3s ease',
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1 style={{ fontSize: '2rem', marginBottom: '20px' }}>Quiz Generator</h1>
            <textarea className="form-control"
                style={{
                    width: '80%',
                    margin: '0 auto', // Align center horizontally
                    marginBottom: '30px',
                    padding: '10px',
                    fontSize: '1rem',
                }}
                rows="4"
                value={inputText}
                onChange={handleInputChange}
            ></textarea>
            <Button
                style={{
                    backgroundColor: '#007bff',
                    display: 'block',
                    margin: '0 auto',
                    width: '45%',
                    color: 'white',
                    border: 'none',
                    padding: '10px 20px',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    transition: 'background-color 0.3s ease',
                }}
                onClick={handleCreateQuiz}
            >
                Create Quiz
            </Button>
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                {showWidget && <Widget />} {/* Conditionally render the Widget component */}
                {questions.map((question, index) => (
                    <div
                        key={index}
                        style={{
                            backgroundColor: '#f0f0f0',
                            borderRadius: '10px',
                            margin: '10px',
                            padding: '20px',
                            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
                            width: '400px',
                        }}
                    >
                        <h3 style={{ fontSize: '1.2rem', marginBottom: '15px' }}>{question.question_statement}</h3>
                        <ul style={{ paddingLeft: '0', listStyleType: 'none' }}>
                            {question.options.map((option, optionIndex) => (
                                <li
                                    key={optionIndex}
                                    className={`option ${option === question.selectedOption ? (question.isCorrect ? 'selected-correct' : 'selected-incorrect') : ''}`}
                                    onClick={() => handleOptionClick(index, option)}
                                    style={{
                                        ...optionStyle,
                                        color: question.isCorrect !== null ? (
                                            option === question.answer ? 'green' : (option === question.selectedOption ? 'red' : 'black')
                                        ) : 'black',
                                        backgroundColor: question.isCorrect !== null && option === question.answer ? '#e6ffe6' : '',
                                    }}
                                >
                                    <input
                                        type="radio"
                                        id={`question-${index}-option-${optionIndex}`}
                                        name={`question-${index}`}
                                        value={option}
                                        checked={option === question.selectedOption}
                                        disabled={question.isCorrect !== null}
                                        readOnly
                                        style={{ marginRight: '10px', cursor: 'not-allowed' }}
                                    />
                                    <label htmlFor={`question-${index}-option-${optionIndex}`} style={{ marginLeft: '5px' }}>{option}</label>
                                </li>
                            ))}
                        </ul>
                        {question.isCorrect !== null && (
                            <p
                                className={`correct-incorrect ${question.isCorrect ? 'correct' : 'incorrect'}`}
                                style={{ fontSize: '1rem', marginTop: '10px', fontWeight: 'bold' }}
                            >
                                {question.isCorrect ? 'Correct Answer' : `Wrong Answer.\n Correct answer: ${question.answer}`}
                            </p>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

