// QuizGenerator.js

import React, { useState } from 'react';
import axios from 'axios';

function QuizGenerator() {
    const [inputText, setInputText] = useState('');
    const [questions, setQuestions] = useState([]);

    const handleCreateQuiz = async () => {
        try {
            const response = await axios.post('http://localhost:5000/generate_questions', { input_text: inputText });
            setQuestions(response.data.questions.map(question => ({ ...question, selectedOption: '', isCorrect: null })));
        } catch (error) {
            console.error('Error generating quiz:', error);
        }
    };

    const handleInputChange = (event) => {
        setInputText(event.target.value);
    };

    const handleOptionChange = (questionIndex, option) => {
        const updatedQuestions = [...questions];
        updatedQuestions[questionIndex].selectedOption = option;
        setQuestions(updatedQuestions);
    };

    const handleCheckAnswer = (questionIndex) => {
        const updatedQuestions = [...questions];
        const question = updatedQuestions[questionIndex];
        if (question.selectedOption !== '') {
            question.isCorrect = question.selectedOption === question.answer;
            setQuestions(updatedQuestions);
        }
    };

    return (
        <div>
            <h1 style={{ textAlign: 'center' }}>Quiz Generator</h1>
            <textarea rows="4" cols="50" value={inputText} onChange={handleInputChange}></textarea>
            <button style={{ display: 'block', margin: '10px auto' }} onClick={handleCreateQuiz}>Create Quiz</button>
            <div>
                {questions.map((question, index) => (
                    <div key={index} style={{ marginBottom: '20px' }}>
                        <h3 style={{ marginBottom: '10px' }}>{question.question_statement}</h3>
                        <ul>
                            {question.options.map((option, optionIndex) => (
                                <li
                                    key={optionIndex}
                                    style={{
                                        marginBottom: '5px'
                                    }}
                                >
                                    <input
                                        type="radio"
                                        id={`question-${index}-option-${optionIndex}`}
                                        name={`question-${index}`}
                                        value={option}
                                        checked={option === question.selectedOption}
                                        onChange={() => handleOptionChange(index, option)}
                                        disabled={question.isCorrect !== null}
                                    />
                                    <label htmlFor={`question-${index}-option-${optionIndex}`} style={{ marginLeft: '5px' }}>{option}</label>
                                </li>
                            ))}
                        </ul>
                        {question.isCorrect !== null && (
                            <p style={{ color: question.isCorrect ? 'green' : 'red' }}>
                                {question.isCorrect ? 'Correct Answer' : `Wrong Answer. Correct answer: ${question.answer}`}
                            </p>
                        )}
                        {!question.isCorrect && (
                            <button onClick={() => handleCheckAnswer(index)}>Check Answer</button>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default QuizGenerator;
