import React, { useState } from 'react';
import axios from 'axios';

const CreateClassroom = () => {
  const [className, setClassName] = useState('');
  const [classDescription, setClassDescription] = useState('');
  const [error, setError] = useState('');

  const handleCreateClassroom = async () => {
    try {
      const response = await axios.post('http://localhost:5000/classrooms', {
        className,
        classDescription
      });
      console.log(response.data);
      // Handle success, e.g., redirect to homepage
    } catch (error) {
      console.error('Error creating classroom:', error);
      setError('Failed to create classroom');
    }
  };

  const handleChange = (e) => {
    setError('');
    const { id, value } = e.target;
    if (id === 'className') {
      setClassName(value);
    } else if (id === 'classDescription') {
      setClassDescription(value);
    }
  };

  return (
    <div>
      <h1>Create Classroom</h1>
      <form>
        <div>
          <label htmlFor="className">Class Name:</label>
          <input type="text" id="className" value={className} onChange={handleChange} />
        </div>
        <div>
          <label htmlFor="classDescription">Class Description:</label>
          <textarea id="classDescription" value={classDescription} onChange={handleChange}></textarea>
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="button" onClick={handleCreateClassroom}>Create Classroom</button>
      </form>
    </div>
  );
}

export default CreateClassroom;
