import React, { useState } from 'react';
import axios from 'axios';

const CreateClassroom = () => {
  const [className, setClassName] = useState('');
  const [classDescription, setClassDescription] = useState('');
  const [error, setError] = useState('');

  const handleCreateClassroom = async () => {
    try {
      const response = await axios.post('http://localhost:5000/classrooms', {
        className,
        classDescription
      });
      console.log(response.data);
      // Handle success, e.g., redirect to homepage
    } catch (error) {
      console.error('Error creating classroom:', error);
      setError('Failed to create classroom');
    }
  };

  
  const handleChange = (e) => {
    setError('');
    const { id, value } = e.target;
    if (id === 'className') {
      setClassName(value);
    } else if (id === 'classDescription') {
      setClassDescription(value);
    }
  };
  return (
    <div>
      <h2>Create Classroom</h2>
      <input type="text" placeholder="Class Name" value={className} onChange={(e) => setClassName(e.target.value)} />
      <textarea placeholder="Class Description" value={classDescription} onChange={(e) => setClassDescription(e.target.value)}></textarea>
      <button onClick={handleCreateClassroom}>Create Classroom</button>
      {error && <p>{error}</p>}
    </div>
  );
}

export default CreateClassroom;
