import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Homepage from './HomePage';

const Results = ({ code, heading, selectedOptions }) => {
    const [quizzes, setQuizzes] = useState([]);
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        const fetchQuizzes = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/quiz/${code}/${heading}`);
                setQuizzes(response.data.quizzes);
                const updatedQuestions = response.data.quizzes.map(quiz => {
                    const correctOption = quiz.options.find(option => option === quiz.answer);
                    return { ...quiz, selectedOption: '', isCorrect: null, correctOption };
                });
                setQuestions(updatedQuestions);
            } catch (error) {
                console.log(error);
            }
        };
        fetchQuizzes();
    }, [code, heading]);

    const { correctCount, wrongCount } = calculateResults();

    function calculateResults() {
        let correctCount = 0;
        let wrongCount = 0;
        for (const questionIndex in selectedOptions) {
            const option = selectedOptions[questionIndex];
            if (option === quizzes[questionIndex].answer) {
                correctCount++;
            } else if (option !== '') {
                wrongCount++;
            }
        }
        return { correctCount, wrongCount };
    }

    return (
        <>
            <Homepage showPlusIcon={false} />
            <div className="max-w-4xl mx-auto p-8">
                <div className="bg-white p-8 rounded-lg shadow-md">
                    <h2 className="font-bold text-2xl mb-6">Quiz Results</h2>
                    <p>Classroom Code: {code}</p>
                    <p>Heading: {heading}</p>
                    <p>Number of Correct Answers: {correctCount}</p>
                    <p>Number of Wrong Answers: {wrongCount}</p>
                    <div className="mt-4">
                        {questions.map((question, index) => (
                            <div key={index} className="bg-white p-4 rounded-lg shadow-md mb-4">
                                <h3 className="font-bold text-lg mb-2">{question.question_statement}</h3>
                                <div className="mt-4">
                                    <p>The options are:</p>
                                    <div className="flex flex-col">
                                        {question.options.map((option, optionIndex) => (
                                            <label key={optionIndex} className="inline-flex items-center mt-3">
                                                <input
                                                    type="radio"
                                                    className="form-radio h-5 w-5 text-zinc-600"
                                                    name={`question-${index}-option`}
                                                    value={option}
                                                    disabled
                                                    checked={option === selectedOptions[index]}
                                                />
                                                <span className={`ml-2 text-zinc-700 ${option === question.correctOption ? 'text-green-500' : (option === selectedOptions[index] ? 'text-blue-500' : '')}`}>
                                                    {option}
                                                </span>
                                            </label>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </>
    );
}

export default Results;
