import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Homepage from './HomePage';

const Classroom = () => {
    // Define state to store fetched data
    const [classrooms, setClassrooms] = useState([]);
    const [className, setClassName] = useState('');
    const [classDescription, setClassDescription] = useState('');
    // Function to fetch data from the server
    const fetchClassrooms = async () => {
        try {
            const response = await axios.get('http://localhost:8000/classrooms' , 
            {
                title: className,
                description: classDescription
            });
            
            // Set the fetched data in the state
            setClassrooms(response.data);
        } catch (error) {
            console.error('Error fetching classrooms:', error);
        }
    };

    // Fetch data when the component mounts
    useEffect(() => {
        fetchClassrooms();
    }, []);

    // Log the fetched data for debugging
    console.log('Classrooms:', classrooms);

    return (
        <>
            {/* Render the homepage or navigation component */}
            <Homepage showPlusIcon={false} />
            {/* Render the fetched data */}
            <div className="container-fluid">
                <div className="row mt-4">
                    {/* Map over the classrooms state to render each item */}
                    {classrooms.length > 0 ? (
                        classrooms.map(classroom => (
                            <div key={classroom._id} className="col-md-4 mb-4">
                                <div className="card">
                                    <div className="card-body">
                                        {/* Display data from each classroom item */}
                                        <h5 className="card-title">{classroom.title}</h5>
                                        <p className="card-text">{classroom.description}</p>
                                    </div>
                                </div>
                            </div>
                        ))
                    ) : (
                        // Show a message if no classrooms are available
                        <p style={{ color: 'red', fontStyle: 'italic', textAlign: 'center' }}>No classrooms available</p>
                    )}
                </div>
            </div>
        </>
    );
};

export default Classroom;
