import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import axios from "axios";

const Join = () => {
    const [className, setClassName] = useState('');
    const [classCode, setClassCode] = useState('');
    const [error, setError] = useState('');
    const [joinedClassroom, setJoinedClassroom] = useState(null);

    const handleJoinClassroom = async () => {
        try {
            const response = await axios.post('http://localhost:8000/classrooms/join', {
                className: className,
                code: classCode
            });
            if (response.status === 200) {
                console.log("Successfully joined the classroom");
                setJoinedClassroom(response.data); // Set joined classroom data
            } else {
                setError('Failed to join classroom');
            }
            window.location.href = '/userclass';

        } catch (error) {
            console.error('Error joining classroom:', error);
            setError('Failed to join classroom');
        }
    };

    useEffect(() => {
        // Fetch joined classroom data if available
        if (joinedClassroom) {
            // Optionally, you can fetch the joined classroom data from the backend using its ID
            console.log("Joined classroom data:", joinedClassroom);
            // Redirect the user to the joined classroom page or display the joined classroom content
            // window.location.href = '/Classroom';
        }
    }, [joinedClassroom]);

    return (
        <div className="container">
            {joinedClassroom ? (
                <div>
                    <h2>Joined Classroom: {joinedClassroom.title}</h2>
                    <p>Description: {joinedClassroom.description}</p>
                    {/* Display other information about the joined classroom */}
                </div>
            ) : (
                <div>
                    <h2>Join Classroom Via Code</h2>
                    {error && <div className="alert alert-danger">{error}</div>}
                    <div className="form-group">
                        <label htmlFor="className">Enter the name</label>
                        <input type="text" className="form-control" id="className" value={className} onChange={(e) => setClassName(e.target.value)} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="classCode">Enter the Code to Join</label>
                        <input type="text" className="form-control" id="classCode" value={classCode} onChange={(e) => setClassCode(e.target.value)} />
                    </div>
                    <Button onClick={handleJoinClassroom}>Join Classroom</Button>
                </div>
            )}
        </div>
    )
}

export default Join;



const styles = `
        .container {
            margin: 20px;
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 5px;
            background-color: #f9f9f9;
        }
        
        .form-group {
            margin-bottom: 15px;
        }
        
        label {
            font-weight: bold;
        }
        
        .form-control {
            width: 100%;
            padding: 8px;
            border: 1px solid #ccc;
            border-radius: 4px;
            box-sizing: border-box;
        }
        
        textarea {
            resize: vertical;
            height: 100px;
        }
        
        .alert {
            margin-bottom: 15px;
            padding: 15px;
            border: 1px solid transparent;
            border-radius: 4px;
        }
        
        .alert-danger {
            color: #721c24;
            background-color: #f8d7da;
            border-color: #f5c6cb;
        }
        button{

            flex-direction: row;
            padding: 18px;
            border: 1px solid black;
          }
    `;

    const styleElement = document.createElement('style');
    styleElement.appendChild(document.createTextNode(styles));
    document.head.appendChild(styleElement);
