// QuizGenerator.js

import React, { useState } from 'react';
import axios from 'axios';

function QuizGenerator() {
    const [inputText, setInputText] = useState('');
    const [questions, setQuestions] = useState([]);

    const handleCreateQuiz = async () => {
        try {
            const response = await axios.post('http://localhost:5000/generate_questions', { input_text: inputText });
            setQuestions(response.data.questions.map(question => ({ ...question, selectedOption: '', isCorrect: null })));
        } catch (error) {
            console.error('Error generating quiz:', error);
        }
    };

    const handleInputChange = (event) => {
        setInputText(event.target.value);
    };

    const handleOptionClick = (questionIndex, option) => {
        const updatedQuestions = [...questions];
        const question = updatedQuestions[questionIndex];
        if (question.selectedOption === '') {
            question.selectedOption = option;
            question.isCorrect = option === question.answer;
        }
        setQuestions(updatedQuestions);
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Quiz Generator</h1>
            <div className="mb-3">
                <textarea className="form-control" rows="4" value={inputText} onChange={handleInputChange}></textarea>
            </div>
            <button className="btn btn-primary" onClick={handleCreateQuiz}>Create Quiz</button>
            <div className="mt-4">
                {questions.map((question, index) => (
                    <div key={index} className="mb-4">
                        <h3>{question.question_statement}</h3>
                        <ul className="list-unstyled">
                            {question.options.map((option, optionIndex) => (
                                <li
                                    key={optionIndex}
                                    onClick={() => handleOptionClick(index, option)}
                                    className={`option ${question.isCorrect !== null ? (
                                        option === question.answer ? 'text-success' : (option === question.selectedOption ? 'text-danger' : 'text-dark')
                                    ) : 'text-dark'}`}
                                >
                                    {option}
                                </li>
                            ))}
                        </ul>
                        {question.isCorrect !== null && (
                            <p className="text-success">Correct Answer: {question.answer}</p>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default QuizGenerator;
