import React, { useState, useEffect, useRef } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button } from 'react-bootstrap';
import axios from 'axios';

const HomePage = ({ showPlusIcon = true }) => {
  const { isAuthenticated, loginWithRedirect, logout, user } = useAuth0();
  const [showDropdown, setShowDropdown] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [classrooms, setClassrooms] = useState([]);

  const dropdownRef = useRef(null);
  const userDropdownRef = useRef(null);
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };
  
  const handleClick = (e) => {
    e.preventDefault();
    window.location.href = '/';
  };
  useEffect(() => {
    const fetchClassrooms = async () => {
      try {
        const response = await axios.get('http://localhost:5000/classrooms');
        setClassrooms(response.data);
      } catch (error) {
        console.error('Error fetching classrooms:', error);
      }
    };

    fetchClassrooms();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
      if (userDropdownRef.current && !userDropdownRef.current.contains(event.target)) {
        setShowUserDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div>
      <nav className="navbar">
        <div className="container" style={{ overflow: 'visible' }}>
          <div className="navbar-brand">
            {isAuthenticated ? (
              <a href="/" style={{ textDecoration: 'none' }} onClick={handleClick}>
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name">EzOuiz</span>
              </a>
            ) : (
              <a href="/" style={{ textDecoration: 'none' }} >
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name">EzOuiz</span>
              </a>
            )}
          </div>
          <div className="navbar-icons">
            {isAuthenticated && showPlusIcon && (
              <div className="dropdown" onClick={toggleDropdown} ref={dropdownRef}>
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#000000" className="bi bi-plus" viewBox="0 0 24 24" style={{ marginLeft: '20' }}>
                  <path d="M11 5h2v14h-2z" />
                  <path d="M5 11h14v2H5z" />
                </svg>

                <div className={`dropdown-menu ${showDropdown ? 'show' : ''}`}>
                  <div className="dropdown-item" onClick={() => window.location.href = '/Create'}>Create Classroom</div>
                  <div className="dropdown-item">Join Classroom</div>
                </div>
              </div>
            )}
            {isAuthenticated && (
              <div className="dropdown" onClick={toggleUserDropdown} style={{ position: 'relative', top: 0, right: 0, marginLeft: "20px" }} ref={userDropdownRef}>
                <img src={user.picture} alt={user.name} style={{ width: 35, height: 35, borderRadius: 50, marginLeft: '20px', cursor: 'pointer' }} />
                <div className={`dropdown-menu ${showUserDropdown ? 'show' : ''}`}>
                  <div className="dropdown-item">Profile</div>
                  <div className="dropdown-item">Settings</div>
                  <div className="dropdown-item" onClick={() => logout({ returnTo: window.location.origin })}>Logout</div>
                </div>
              </div>
            )}
            {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
          </div>
        </div>
      </nav>
      <div className="container-fluid " >
             {/* Pass showPlusIcon prop with value false */}
            <div className="row mt-4">
                {classrooms.length > 0 ? (
                    classrooms.map(classroom => (
                        <div key={classroom._id} className="col-md-4 mb-4">
                            <div className="card">
                                <div className="card-body">
                                    <h5 className="card-title">{classroom.title}</h5>
                                    <p className="card-text">{classroom.description}</p>
                                    
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p style={{ color: 'red', fontStyle: 'italic', textAlign: 'center' ,fontSize:'20px'}}>No classrooms available</p>
                )}
            </div>
        </div>
    </div>
  );
}

export default HomePage;
const styles = `
/* Navbar styles */
.navbar {
  background-color: white; /* Change the navbar background color to white */
  color: #000; /* Change the text color to black */
  padding: 10px 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 2px 4px rgba(0, 0, 0, 1); /* Add light shadow to the bottom */
  width: 100%;
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
  font-size:25px;
  font-weight:500;
color:black;
}

/* Navbar icons styles */
.navbar-icons {
  display: flex;
  align-items: center;
}

/* Plus icon styles */
.icon-btn {
  background-color: transparent;
  border: none;
  font-size: 18px;
  cursor: pointer;
  margin-left: 10px;
}

/* User avatar styles */
.avatar {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-left: 10px;
  cursor: pointer;
}
.dropdown:hover {
  
  cursor: pointer;
}
/* Media queries for responsiveness */
@media screen and (max-width: 768px) {
  /* Adjust styles for smaller screens */
  .navbar {
    flex-direction: column;
    align-items: flex-start;
  }
  
  .brand-name {
    margin-left: 0;
    margin-top: 10px;
  }
  
  .navbar-icons {
    margin-top: 10px;
  }
}

body{
  background-color: antiquewhite;
}

.container{
  margin:0;
  padding: 0;
  font-family: 'Times New Roman', Times, serif;
}
h1{
  text-align: center;
  justify-content: center;
  display: flex;
  flex-wrap: wrap;
}
button{

  flex-direction: row;
  padding: 18px;
  border: 1px solid black;
}
/* User avatar styles */
.avatar {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-left: 10px;
  cursor: pointer;
}
`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
