import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button, Dropdown } from 'react-bootstrap';

const HomePage = () => {
  const { isAuthenticated, loginWithRedirect, logout, user } = useAuth0(); // Access user information from useAuth0 hook
  const [showDropdown, setShowDropdown] = useState(false); // State to control visibility of plus icon dropdown
  const [showUserDropdown, setShowUserDropdown] = useState(false); // State to control visibility of user avatar dropdown

  // Function to toggle visibility of plus icon dropdown
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  // Function to toggle visibility of user avatar dropdown
  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };

  const HomePage = () => {
    const [classrooms, setClassrooms] = useState([]);

  useEffect(() => {
    // Load FontAwesome CSS
    const link = document.createElement('link');
    link.href = "https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css";
    link.rel = "stylesheet";
    document.head.appendChild(link);

    return () => {
      // Cleanup on unmount
      document.head.removeChild(link);
    };
  }, []);



  return (

    <div>

      <nav className="navbar">

        <div className="container">
          <div className="navbar-brand">
            {/* Conditionally render navbar brand link */}
            {isAuthenticated ? (
              <a href="/" style={{ textDecoration: 'none' }} onClick={(e) => { e.preventDefault(); }}>
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name" sx={{ fontSize: 50 }}>EzOuiz</span>
              </a>
            ) : (
              <a href="/" style={{ textDecoration: 'none' }}>
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name" sx={{ fontSize: 50 }}>EzOuiz</span>
              </a>
            )}
          </div>
          <div className="navbar-icons">

            {isAuthenticated && (
              <Dropdown show={showDropdown} onClick={toggleDropdown}>
                <Dropdown className="dropdown" id="plus-dropdown">
                  <i className="fas fa-plus" style={{ color: 'black', marginRight: '17px' }}></i>
                </Dropdown>
                <Dropdown.Menu>
                  <Dropdown.Item style={{ color: 'blue' }} href="/Create">Create Class</Dropdown.Item>
                  <Dropdown.Divider />
                  <Dropdown.Item style={{ color: 'blue' }} href="#">Join Class</Dropdown.Item>
                  {/* Add more options as needed */}
                </Dropdown.Menu>
              </Dropdown>
            )}
            {/* User Avatar Dropdown */}
            {isAuthenticated && (
              <Dropdown show={showUserDropdown} onClick={toggleUserDropdown} style={{ marginLeft: '20px' }}>
                <Dropdown className="dropdown" id="user-dropdown" >
                  <img src={user.picture} alt={user.name} style={{ width: 35, height: 35, borderRadius: 50 }} />
                </Dropdown>
                <Dropdown.Menu >
                  <Dropdown.Item style={{ color: 'blue' }} href="#">Profile</Dropdown.Item>
                  <Dropdown.Item style={{ color: 'blue' }} href="#">Settings</Dropdown.Item>
                  <Dropdown.Divider />
                  <Dropdown.Item style={{ color: 'red' }} href="#" onClick={() => logout({ returnTo: window.location.origin })}>Logout</Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            )}
            {/* Login Button */}
            {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
          </div>

        </div>


      </nav>


      <div className='container-fluid'>
        <h1 style={{ textAlign: 'center' }}>Welcome to the EzQuiz </h1>
        {isAuthenticated && (
          <div>
          {classrooms.map(classroom => (
            <div key={classroom.classroom_id}>
              <h2>{classroom.name}</h2>
              <p>{classroom.description}</p>
            </div>
          ))}
        </div>
        )}
      </div>

    </div>

  );

}
}

export default HomePage;

// CSS for navbar styles
const styles = `
/* Navbar styles */
.navbar {
  background-color: white; /* Change the navbar background color to white */
  color: #000; /* Change the text color to black */
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 2px 4px rgba(0, 0, 0, 1); /* Add light shadow to the bottom */
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
  font-size:25px;
  font-weight:500;
color:black;
}

/* Navbar icons styles */
.navbar-icons {
  display: flex;
  align-items: center;
}

/* Plus icon styles */
.icon-btn {
  background-color: transparent;
  border: none;
  font-size: 18px;
  cursor: pointer;
  margin-left: 10px;
}

/* User avatar styles */
.avatar {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-left: 10px;
  cursor: pointer;
}
.dropdown:hover {
  
  cursor: pointer;
}
/* Media queries for responsiveness */
@media screen and (max-width: 768px) {
  /* Adjust styles for smaller screens */
  .navbar {
    flex-direction: column;
    align-items: flex-start;
  }
  
  .brand-name {
    margin-left: 0;
    margin-top: 10px;
  }
  
  .navbar-icons {
    margin-top: 10px;
  }
}
`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
