import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Auth0Provider, useAuth0 } from '@auth0/auth0-react';
import CircularProgress from '@material-ui/core/CircularProgress'; // Import CircularProgress component
import HomePage from './Components/HomePage.jsx';
import Classroom from './Components/Classroom.jsx';
import Create from './Components/Create.jsx';
import QuizGenerator from './Components/QuizGenerator.jsx';
import UnauthorizedPage from './Components/UnauthorizedPage.jsx';

const App = () => {
  const { isAuthenticated, isLoading } = useAuth0();

  if (isLoading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress /> {/* Render CircularProgress while loading */}
      </div>
    );
  }

  return (
    <Router>
      <Routes>
        <Route path="/" element={isAuthenticated ? <HomePage /> : <Navigate to="/unauthorized" />} />
        <Route path="/unauthorized" element={<UnauthorizedPage />} />
        {isAuthenticated && (
          <>
            <Route path="/generate" element={<QuizGenerator />} />
            <Route path="/Classroom" element={<Classroom />} />
            <Route path="/Create" element={<Create />} />
          </>
        )}
      </Routes>
    </Router>
  );
};

const domain = "dev-147umiv5h85hvjw1.us.auth0.com";
const clientId = "FH2Q3oyoJZfKm1NbPBrWhH4PGs5BlnlE";

const AppWithAuth0 = () => (
  <Auth0Provider
    domain={domain}
    clientId={clientId}
    redirectUri={window.location.origin}
  >
    <App />
  </Auth0Provider>
);

export default AppWithAuth0;
