import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button } from 'react-bootstrap';

const HomePage = () => {
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const [showOptions, setShowOptions] = useState(false); // State to control visibility of additional options

  // Function to toggle visibility of additional options
  const toggleOptions = () => {
    setShowOptions(!showOptions);
  };

  useEffect(() => {
    // Load FontAwesome CSS
    const link = document.createElement('link');
    link.href = "https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css";
    link.rel = "stylesheet";
    document.head.appendChild(link);

    return () => {
      // Cleanup on unmount
      document.head.removeChild(link);
    };
  }, []);

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="container">
          <div className="navbar-brand">
            <img src="/docs/5.3/assets/brand/bootstrap-logo.svg" alt="Bootstrap" width="30" height="24" />
            <span className="brand-name">Your Brand</span>
          </div>
          <div className="navbar-icons">
            {/* + Icon for toggling additional options */}
            <button className="icon-btn" onClick={toggleOptions}>
              <i className="fas fa-plus"></i> {/* FontAwesome plus icon */}
            </button>
            {/* Additional options */}
            {showOptions && (
              <div className="dropdown">
                <button className="dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                  Options
                </button>
                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                  <a className="dropdown-item" href="#">Option 1</a>
                  <a className="dropdown-item" href="#">Option 2</a>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>

      {/* Your existing content */}
      <div className='container-fluid'>
        <h1 style={{ textAlign: 'center' }}>Welcome to the EzQuiz </h1>
        {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
        {isAuthenticated && (
          <div>
            {/* Dashboard with options for enrolled classes, create class, etc. */}
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;

// CSS for navbar styles
const styles = `
/* Navbar styles */
.navbar {
  background-color: #007bff; /* Change the navbar background color */
  color: #fff;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
}

/* Navbar icons styles */
.navbar-icons {
  display: flex;
  align-items: center;
}

/* Icon button styles */
.icon-btn {
  background-color: transparent;
  border: none;
  color: #fff; /* Change the color of the icon */
  font-size: 18px;
  cursor: pointer;
  margin-left: 10px;
}

/* Dropdown styles */
.dropdown {
  position: relative;
}

.dropdown-toggle::after {
  display: none; /* Hide the down arrow */
}

.dropdown-menu {
  position: absolute;
  top: 100%; /* Position the dropdown menu below the button */
  left: 0;
  z-index: 1000; /* Ensure dropdown is above other elements */
  display: none; /* Hide by default */
  float: left;
  min-width: 10rem;
  padding: 0.5rem 0;
  margin: 0.125rem 0 0;
  font-size: 1rem;
  color: #212529;
  text-align: left;
  list-style: none;
  background-color: #fff;
  background-clip: padding-box;
  border: 1px solid rgba(0, 0, 0, 0.15);
  border-radius: 0.25rem;
}

.dropdown-menu.show {
  display: block; /* Show when dropdown is open */
}
`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
