import React from 'react';
import ClassroomForm from './ClassroomForm';

const CreateClassroom = ({ onCreateClassroom }) => {
  const handleCreateClassroom = classroomData => {
    // Call the onCreateClassroom function passed from the parent component
    onCreateClassroom(classroomData);
  };

  const styles = {
    container: {
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f9f9f9',
      borderRadius: '8px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    },
    heading: {
      marginBottom: '20px',
      fontSize: '24px',
      color: '#333',
    },
    form: {
      padding: '20px',
      backgroundColor: '#fff',
      borderRadius: '8px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Create a New Classroom</h2>
      <div style={styles.form}>
        <ClassroomForm onCreateClassroom={handleCreateClassroom} />
      </div>
    </div>
  );
};

export default CreateClassroom;
