import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Assuming you're using React Router for navigation
import { useAuth0 } from '@auth0/auth0-react'; // Import useAuth0 hook from Auth0

const Navbar = () => {
  const { loginWithRedirect, logout, isAuthenticated, user } = useAuth0(); // Destructure auth0 functions and state
  const [profileImageUrl, setProfileImageUrl] = useState(null); // State to store profile image URL

  useEffect(() => {
    if (isAuthenticated && user.picture) {
      setProfileImageUrl(user.picture); // Set profile image URL if user is authenticated and has a picture
    }
  }, [isAuthenticated, user]);

  return (
    <nav className="navbar">
      <div className="navbar__logo">
        <Link to="/">
          <img src="logo.png" alt="Logo" />
        </Link>
      </div>
      <div className="navbar__actions">
        <div className="navbar__dropdown">
          <button className="navbar__dropdown-btn">
            +
            <div className="navbar__dropdown-content">
              <Link to="/create">Create</Link>
              <Link to="/edit">Edit</Link>
            </div>
          </button>
        </div>
        {isAuthenticated ? (
          <div className="navbar__profile">
            {profileImageUrl && (
              <img src={profileImageUrl} alt="Profile Avatar" />
            )}
            <div className="navbar__dropdown-content">
              <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>
            </div>
          </div>
        ) : (
          <button className="navbar__login-btn" onClick={() => loginWithRedirect()}>Login</button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
