// Results.js

import React from 'react';
import { useLocation, useParams } from 'react-router-dom';
import Homepage from './HomePage';

const Results = () => {
    const { code, heading } = useParams();
    const location = useLocation();
    const { questions, selectedOptions } = location.state || {}; // Use optional chaining to handle undefined state

    const { correctCount, wrongCount } = calculateResults();

    function calculateResults() {
        let correctCount = 0;
        let wrongCount = 0;
        for (const questionIndex in selectedOptions || {}) { // Use optional chaining to handle undefined selectedOptions
            const option = selectedOptions[questionIndex];
            if (option === true) {
                correctCount++;
            } else if (option === false) {
                wrongCount++;
            }
        }
        return { correctCount, wrongCount };
    }

    return (
        <>
            <Homepage showPlusIcon={false} />
            <div className="max-w-4xl mx-auto p-8">
                <div className="bg-white p-8 rounded-lg shadow-md">
                    <h2 className="font-bold text-2xl mb-6">Quiz Results</h2>
                    <p>Classroom Code: {code}</p>
                    <p>Heading: {heading}</p>
                    <p>Number of Correct Answers: {correctCount}</p>
                    <p>Number of Wrong Answers: {wrongCount}</p>
                    <div className="mt-4">
                        {questions?.map((question, index) => ( // Use optional chaining to handle undefined questions
                            <div key={index} className="bg-white p-4 rounded-lg shadow-md mb-4">
                                <h3 className="font-bold text-lg mb-2">{question.question_statement}</h3>
                                <div className="mt-4">
                                    <p>The options are:</p>
                                    <div className="flex flex-col">
                                        {question.options?.map((option, optionIndex) => ( // Use optional chaining to handle undefined options
                                            <label key={optionIndex} className="inline-flex items-center mt-3">
                                                <input
                                                    type="radio"
                                                    className="form-radio h-5 w-5 text-zinc-600"
                                                    name={`question-${index}-option`}
                                                    value={option}
                                                    checked={selectedOptions?.[index] === option} // Use optional chaining to handle undefined selectedOptions
                                                    disabled
                                                />
                                                <span className={`ml-2 text-zinc-700 ${selectedOptions?.[index] === option ? (question.correctOption === option ? 'text-green-500' : 'text-red-500') : ''}`}>
                                                    {option}
                                                </span>
                                            </label>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </>
    );
}

export default Results;
