import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button, Dropdown } from 'react-bootstrap'; 

const HomePage = () => {
  const { isAuthenticated, loginWithRedirect, logout, user } = useAuth0(); // Access user information from useAuth0 hook
  const [showDropdown, setShowDropdown] = useState(false); // State to control visibility of plus icon dropdown
  const [showUserDropdown, setShowUserDropdown] = useState(false); // State to control visibility of user avatar dropdown

  // Function to toggle visibility of plus icon dropdown
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  // Function to toggle visibility of user avatar dropdown
  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };

  useEffect(() => {
    // Load FontAwesome CSS
    const link = document.createElement('link');
    link.href = "https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css";
    link.rel = "stylesheet";
    document.head.appendChild(link);

    return () => {
      // Cleanup on unmount
      document.head.removeChild(link);
    };
  }, []);

 

  return (
    <div>
      {/* Navbar */}
      {isAuthenticated && (
  <div className="navbar-icons">
    {/* Plus Icon Dropdown */}
    <Dropdown show={showDropdown} onClick={toggleDropdown}>
      <Dropdown.Toggle variant="link" id="dropdown-basic">
        <i className="fas fa-plus" style={{ color: 'white' }}></i>
      </Dropdown.Toggle>
      <Dropdown.Menu>
        <Dropdown.Item href="#">Option 1</Dropdown.Item>
        <Dropdown.Item href="#">Option 2</Dropdown.Item>
        {/* Add more options as needed */}
      </Dropdown.Menu>
    </Dropdown>
    {/* User Avatar Dropdown */}
    <Dropdown show={showUserDropdown} onClick={toggleUserDropdown}>
      <Dropdown.Toggle variant="link" id="user-dropdown">
        <img src={user.picture} alt={user.name} style={{ width: 35, height: 35, borderRadius: 50 }} />
      </Dropdown.Toggle>
      <Dropdown.Menu>
        <Dropdown.Item href="#">Profile</Dropdown.Item>
        <Dropdown.Item href="#">Settings</Dropdown.Item>
        <Dropdown.Divider />
        <Dropdown.Item href="#" onClick={() => logout({ returnTo: window.location.origin })}>Logout</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  </div>
)}

      {/* Your existing content */}
      <div className='container-fluid'>
        <h1 style={{ textAlign: 'center' }}>Welcome to the EzQuiz </h1>
        {isAuthenticated && (
          <div>
            {/* Dashboard with options for enrolled classes, create class, etc. */}
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;

// CSS for navbar styles
const styles = `
/* Navbar styles */
.navbar {
  background-color: #007bff; /* Change the navbar background color */
  color: #fff;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
  font-size:25px;
  font-weight:500;
  color:white;
}

/* Navbar icons styles */
.navbar-icons {
  display: flex;
  align-items: center;
}

/* Plus icon styles */
.icon-btn {
  background-color: transparent;
  border: none;
  font-size: 18px;
  cursor: pointer;
  margin-left: 10px;
}

/* User avatar styles */
.avatar {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-left: 10px;
  cursor: pointer;
}

/* Media queries for responsiveness */
@media screen and (max-width: 768px) {
  /* Adjust styles for smaller screens */
  .navbar {
    flex-direction: column;
    align-items: flex-start;
  }
  
  .brand-name {
    margin-left: 0;
    margin-top: 10px;
  }
  
  .navbar-icons {
    margin-top: 10px;
  }
}
`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
