import React, { useState } from 'react';
import ClassroomForm from './ClassroomForm';

const CreateClassroom = ({ CreateClassroom }) => {
  const [className, setClassName] = useState('');
  const [classDescription, setClassDescription] = useState('');
  const [error, setError] = useState('');

  const handleCreateClassroom = () => {
    if (!className.trim() || !classDescription.trim()) {
      setError('Please fill in all fields');
      return;
    }

    // Call the onCreateClassroom function passed from the parent component
    handleCreateClassroom({ className, classDescription });
  };

  const handleChange = (e) => {
    setError('');
    const { id, value } = e.target;
    if (id === 'className') {
      setClassName(value);
    } else if (id === 'classDescription') {
      setClassDescription(value);
    }
  };

  const styles = {
    container: {
      maxWidth: '600px',
      margin: '0 auto',
      padding: '20px',
      backgroundColor: '#f9f9f9',
      borderRadius: '8px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    },
    heading: {
      marginBottom: '20px',
      fontSize: '24px',
      color: '#333',
    },
    form: {
      padding: '20px',
      backgroundColor: '#fff',
      borderRadius: '8px',
      boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    },
    formGroup: {
      marginBottom: '20px',
    },
    error: {
      color: 'red',
      marginBottom: '10px',
    },
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Create a New Classroom</h2>
      <div style={styles.form}>
        {error && <p style={styles.error}>{error}</p>}
        <div className="form-group" style={styles.formGroup}>
          <label htmlFor="className">Class Name</label>
          <input type="text" className="form-control" id="className" value={className} onChange={handleChange} placeholder="Enter class name" />
        </div>
        <div className="form-group" style={styles.formGroup}>
          <label htmlFor="classDescription">Class Description</label>
          <textarea className="form-control" id="classDescription" value={classDescription} onChange={handleChange} rows="3" placeholder="Enter class description"></textarea>
        </div>
        <button type="submit" className="btn btn-primary" onClick={handleCreateClassroom}>Create Classroom</button>
      </div>
    </div>
  );
};

export default CreateClassroom;
