import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button } from 'react-bootstrap';

const HomePage = () => {
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const [showOptions, setShowOptions] = useState(false); // State to control visibility of additional options

  // Function to toggle visibility of additional options
  const toggleOptions = () => {
    setShowOptions(!showOptions);
  };

  useEffect(() => {
    // Load FontAwesome CSS
    const link = document.createElement('link');
    link.href = "https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css";
    link.rel = "stylesheet";
    document.head.appendChild(link);

    return () => {
      // Cleanup on unmount
      document.head.removeChild(link);
    };
  }, []);

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="container">
          <div className="navbar-brand">
            <img src="/docs/5.3/assets/brand/bootstrap-logo.svg" alt="Bootstrap" width="30" height="24" />
            <span className="brand-name">Your Brand</span>
          </div>
          <div className="navbar-icons">
            {/* + Icon for toggling additional options */}
            <button className="icon-btn" onClick={toggleOptions}>
              <i className="fas fa-plus"></i> {/* FontAwesome plus icon */}
            </button>
            {/* Additional options */}
            {showOptions && (
              <div className="options">
                <ul>
                  <li>Option 1</li>
                  <li>Option 2</li>
                  {/* Add more options as needed */}
                </ul>
              </div>
            )}
          </div>
        </div>
      </nav>

      {/* Your existing content */}
      <div className='container-fluid'>
        <h1 style={{ textAlign: 'center' }}>Welcome to the EzQuiz </h1>
        {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
        {isAuthenticated && (
          <div>
            {/* Dashboard with options for enrolled classes, create class, etc. */}
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;

// CSS for navbar styles
const styles = `
/* Navbar styles */
.navbar {
  background-color: #007bff; /* Change the navbar background color */
  color: #fff;
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
}

/* Navbar icons styles */
.navbar-icons {
  display: flex;
  align-items: center;
  position: relative; /* Set position relative for positioning the options */
}

/* Icon button styles */
.icon-btn {
  background-color: transparent;
  border: none;
  color: #fff; /* Change the color of the icon */
  font-size: 18px;
  cursor: pointer;
  margin-left: 10px;
}

/* Additional options styles */
.options {
  position: absolute;
  bottom: calc(100% + 10px); /* Position options container below the icon */
  right: 0;
  background-color: #fff; /* Change the background color of the options */
  color: #333; /* Change the text color of the options */
  padding: 20px;
  border: 1px solid #ccc; /* Add border for better visibility */
  border-radius: 5px;
  display: flex;
  height:100
  flex-direction: column;
  z-index: 1000; /* Ensure options appear above other elements */
}

.options ul {
  list-style-type: none;
  padding: 0;
}

.options ul li {
  margin-bottom: 5px;
}

/* Media queries for responsiveness */
@media screen and (max-width: 768px) {
  /* Adjust styles for smaller screens */
  .navbar {
    flex-direction: column;
    align-items: flex-start;
  }
  
  .brand-name {
    margin-left: 0;
    margin-top: 10px;
  }
  
  .navbar-icons {
    margin-top: 10px;
  }
}
`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
