import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button } from 'react-bootstrap';
import { FaPlus } from 'react-icons/fa'; // Import the plus icon from React Icons
import axios from 'axios';

const HomePage = () => {
  const { isAuthenticated, loginWithRedirect, logout, user } = useAuth0();
  const [showDropdown, setShowDropdown] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [classrooms, setClassrooms] = useState([]);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };

  useEffect(() => {
    const fetchClassrooms = async () => {
      try {
        const response = await axios.get('http://localhost:5000/classrooms');
        setClassrooms(response.data);
      } catch (error) {
        console.error('Error fetching classrooms:', error);
      }
    };

    fetchClassrooms();
  }, []);

  return (
    <div>
      <nav className="navbar">
        <div className="container">
          <div className="navbar-brand">
            {isAuthenticated ? (
              <a href="/" style={{ textDecoration: 'none' }} onClick={(e) => { e.preventDefault(); }}>
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name">EzOuiz</span>
              </a>
            ) : (
              <a href="/" style={{ textDecoration: 'none' }}>
                <img src="./images/logo.png" alt="logo" width="50" height="50" />
                <span className="brand-name">EzOuiz</span>
              </a>
            )}
          </div>
          <div className="navbar-icons">
            {isAuthenticated && (
              <div className="dropdown" onClick={toggleDropdown}>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="#000000" className="bi bi-plus" viewBox="0 0 16 10">
                  <path d="M7.5 0c.368 0 .679.248.791.594L8 1.5v6.5h6.5a.5.5 0 0 1 0 1H8v6.5a.5.5 0 0 1-1 0V9H1.5a.5.5 0 0 1 0-1H7V1.5a.5.5 0 0 1 .5-.5z" />
                </svg>

                <div className={`dropdown-menu ${showDropdown ? 'show' : ''}`}>
                  <div className="dropdown-item" onClick='/classrooms'>Create Classroom</div>
                  <div className="dropdown-item">Join Classroom</div>
                </div>
              </div>
            )}
            {isAuthenticated && (
              <div className="dropdown" onClick={toggleUserDropdown} style={{ marginLeft: '20px' }}>
                <img src={user.picture} alt={user.name} style={{ width: 35, height: 35, borderRadius: 50, cursor: 'pointer' }} />
                <div className={`dropdown-menu ${showUserDropdown ? 'show' : ''}`}>
                  <div className="dropdown-item">Profile</div>
                  <div className="dropdown-item">Settings</div>
                  <div className="dropdown-item" onClick={() => logout({ returnTo: window.location.origin })}>Logout</div>
                </div>
              </div>
            )}
            {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
          </div>
        </div>
      </nav>
      <div className='container-fluid'>
        <h1 style={{ textAlign: 'center' }}>Welcome to the EzQuiz</h1>
        {isAuthenticated && (
          <div>
            {classrooms.map(classroom => (
              <div key={classroom.classroom_id}>
                <h2>{classroom.name}</h2>
                <p>{classroom.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default HomePage;
const styles = `
/* Navbar styles */
.navbar {
  background-color: white; /* Change the navbar background color to white */
  color: #000; /* Change the text color to black */
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 2px 4px rgba(0, 0, 0, 1); /* Add light shadow to the bottom */
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
  font-size:25px;
  font-weight:500;
color:black;
}

/* Navbar icons styles */
.navbar-icons {
  display: flex;
  align-items: center;
}

/* Plus icon styles */
.icon-btn {
  background-color: transparent;
  border: none;
  font-size: 18px;
  cursor: pointer;
  margin-left: 10px;
}

/* User avatar styles */
.avatar {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-left: 10px;
  cursor: pointer;
}
.dropdown:hover {
  
  cursor: pointer;
}
/* Media queries for responsiveness */
@media screen and (max-width: 768px) {
  /* Adjust styles for smaller screens */
  .navbar {
    flex-direction: column;
    align-items: flex-start;
  }
  
  .brand-name {
    margin-left: 0;
    margin-top: 10px;
  }
  
  .navbar-icons {
    margin-top: 10px;
  }
}
`;

// Inject the CSS styles into the document
const styleElement = document.createElement('style');
styleElement.appendChild(document.createTextNode(styles));
document.head.appendChild(styleElement);
