          <div>
            {classrooms.map(classroom => (
              <div key={classroom.classroom_id}>
                <h2>{classroom.name}</h2>
                <p>{classroom.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
  </>
  )
}

export default Classroom

const styles = `
/* Navbar styles */
.navbar {
  background-color: white; /* Change the navbar background color to white */
  color: #000; /* Change the text color to black */
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 2px 4px rgba(0, 0, 0, 1); /* Add light shadow to the bottom */
}

/* Brand name styles */
.brand-name {
  margin-left: 10px;
  font-size:25px;
  font-weight:500;
color:black;
}



.dropdown:hover {
  
  cursor: pointer;
}
/* Media queries for responsiveness */
@media screen and (max-width: 768px) {
  /* Adjust styles for smaller screens */
  
  
  .brand-name {
    margin-left: 0;
    margin-top: 10px;
  }
  
  .navbar-icons {
    margin-top: 10px;
  }
}

body{
  background-color: antiquewhite;
}

.container{
  margin:0;
  padding: 0;
  font-family: 'Times New Roman', Times, serif;
}


`;

