// Frontend Logic - Quizzes.js

import React, { useEffect, useState } from 'react';
import Homepage from './HomePage';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Quizzes = () => {
    const { code } = useParams();
    const [quizzes, setQuizzes] = useState([]);

    const fetchQuizzesByClassCode = async () => {
        try {
            const response = await axios.get(`http://localhost:8000/quizzes/classrooms/bycode/${code}`);
            setQuizzes(response.data.quizzes);
        } catch (error) {
            console.log(error);
        }
    };

    const generateQuiz = async () => {
        try {
            const response = await axios.post(`http://localhost:8000/quizzes/generate`, { input_text: 'Some text', code });
            setQuizzes(response.data.quiz);
        } catch (error) {
            console.log(error);
        }
    };

    useEffect(() => {
        fetchQuizzesByClassCode();
    }, [code]); // Fetch quizzes whenever the class code changes

    return (
        <>
            <Homepage showPlusIcon={false} />
            <button onClick={generateQuiz}>Generate Quiz</button>
            <div className="quizzes-container">
                {quizzes.length > 0 ? (
                    quizzes.map((quiz, index) => (
                        <div key={index} className="quiz">
                            <h2>Quiz {index + 1}</h2>
                            <ul>
                                {quiz.map((question, qIndex) => (
                                    <li key={qIndex}>{question}</li>
                                ))}
                            </ul>
                        </div>
                    ))
                ) : (
                    <h1>No quizzes available for this class</h1>
                )}
            </div>
        </>
    );
};

export default Quizzes;
