import React, { useState, useEffect, useRef } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Button } from 'react-bootstrap';
import axios from 'axios';
import { Card } from 'react-bootstrap';



const HomePage = ({ showPlusIcon = true }) => {
  const { isAuthenticated, loginWithRedirect, logout, user } = useAuth0();
  const [showDropdown, setShowDropdown] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const [classrooms, setClassrooms] = useState([]);

  const dropdownRef = useRef(null);
  const userDropdownRef = useRef(null);
  
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const toggleUserDropdown = () => {
    setShowUserDropdown(!showUserDropdown);
  };
  
  const handleClick = (e) => {
    e.preventDefault();
    window.location.href = '/';
  };

  

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
      if (userDropdownRef.current && !userDropdownRef.current.contains(event.target)) {
        setShowUserDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);



  const fetchClassrooms = async () => {
    try {
        const response = await axios.get('http://localhost:8000/classrooms');
        console.log(response.data.classrooms)
        // Set the fetched data in the state
        setClassrooms(response.data.classrooms);
        console.log("d :: ", classrooms)
    } catch (error) {
        console.error('Error fetching classrooms:', error);
    }
};

// Fetch data when the component mounts
useEffect(() => {
    fetchClassrooms();
}, []);



  return (
    <>
    <div>
      <nav className="navbar flex justify-between items-center bg-white p-3 shadow ">
        <div className="navbar-brand">
          {isAuthenticated ? (
            <a href="/" className="flex items-center" onClick={handleClick}>
              <img src="./images/logo.png" alt="logo" className="w-10 h-10" />
              <span className="brand-name text-xl font-semibold ml-2">EzOuiz</span>
            </a>
          ) : (
            <a href="/" className="flex items-center">
              <img src="./images/logo.png" alt="logo" className="w-10 h-10" />
              <span className="brand-name text-xl font-semibold ml-2">EzOuiz</span>
            </a>
          )}
        </div>
        <div className="navbar-icons flex items-center">
        {isAuthenticated && (
            <a href="/Userclass" className="mr-4">View Classrooms</a> 
          )}
          {isAuthenticated && showPlusIcon && (
            <div className="dropdown relative" onClick={toggleDropdown} ref={dropdownRef}>
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#000000" className="bi bi-plus w-6 h-6 mr-2" viewBox="0 0 24 24">
                <path d="M11 5h2v14h-2z" />
                <path d="M5 11h14v2H5z" />
              </svg>
              <div className={`dropdown-menu ${showDropdown ? 'block' : 'hidden'} absolute top-full right-0 mt-2 bg-white border border-gray-200 rounded shadow-lg`}>
                <div className="dropdown-item py-2 px-4 cursor-pointer" onClick={() => window.location.href = '/Create'}>Create Classroom</div>
                <div className="dropdown-item py-2 px-4 cursor-pointer" onClick={() => window.location.href = '/join'}>Join Classroom</div>
              </div>
            </div>
          )}
          {isAuthenticated && (
            <div className="dropdown relative" onClick={toggleUserDropdown} ref={userDropdownRef}>
              <img src={user.picture} alt={user.name} className="avatar w-8 h-8 rounded-full cursor-pointer ml-2" />
              <div className={`dropdown-menu ${showUserDropdown ? 'block' : 'hidden'} absolute top-full right-0 mt-2 bg-white border border-gray-200 rounded shadow-lg`}>
                <div className="dropdown-item py-2 px-4 cursor-pointer">Profile</div>
                <div className="dropdown-item py-2 px-4 cursor-pointer">Settings</div>
                <div className="dropdown-item py-2 px-4 cursor-pointer" onClick={() => logout({ returnTo: window.location.origin })}>Logout</div>
              </div>
            </div>
          )}
          {!isAuthenticated && <Button onClick={loginWithRedirect}>Log In</Button>}
        </div>
      </nav>
      
    </div>

<div className="min-h-screen main p-0 " >
                    <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 row-cols-xl-4 g-4">
                        {classrooms.length > 0 ? (
                            classrooms.map(classroom => (
                                <div key={classroom._id}>
                                    <Card className="bg-white dark:bg-zinc-700 rounded-lg shadow-md overflow-hidden">
                                        <Card.Body className="bg-primary text-white">
                                            <Card.Title className="text-lg font-bold">{classroom.title}</Card.Title>
                                            <Card.Text>{classroom.description}</Card.Text>
                                        </Card.Body>
                                        <div className="p-4 border-t border-zinc-200 dark:border-zinc-600">
                                            <div className="flex items-center mb-4" style={{alignItems:'center'}}>
                                                <img style={{width:60 ,height:60, borderRadius:50}} class="w-16 h-16 rounded-full" src={classroom.profile} alt="Teacher Image"/>
                                                    <div className="ml-3" style={{display:'inline-block', marginLeft:20, alignItems:'center'}}>
                                                        <h3 className="font-semibold text-s dark:text-black-300">{classroom.name}</h3>
                                                    </div>
                                            </div>
                                            <Card.Footer className="bg-light border-top">
                                                <div className="d-flex justify-content-between align-items-center">
                                                    <div className="ml-3">
                                                        <h3 className="font-semibold">{classroom.teacher}</h3>
                                                        <h2 className='font-light'>{classroom.code}</h2>
                                                    </div>
                                                    <Button variant="primary" >View</Button>
                                                </div>
                                            </Card.Footer>
                                            </div>
                                    </Card>
                                </div>
                            ))
                        ) : (
                            <p style={{ color: 'red', fontStyle: 'italic', textAlign: 'center' }}>No classrooms available</p>
                        )}
                    </div>
                </div>
                </>

  );
}

export default HomePage;
