import React, { useEffect, useState } from 'react'
import axios from 'axios';
const ClassroomCards = () => {
    const [classrooms, setClassrooms] = useState([]);

    const fetchClassrooms = async () => {
        try {
            const response = await axios.get('http://localhost:8000/classroom');
            console.log(response.data.classrooms)
            // Set the fetched data in the state
            setClassrooms(response.data.classrooms);
            console.log("d :: ", classrooms)
        } catch (error) {
            console.error('Error fetching classrooms:', error);
        }
    };
    useEffect(() => {
        fetchClassrooms();
    }, []);
    return (
        <>
        
            <div className="min-h-screen bg-white dark:bg-zinc-800 p-6">
                <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
                    {classrooms.length > 0 ? (
                        classrooms.map(classroom => (

                            <div key={classroom._id} className="bg-white dark:bg-zinc-700 rounded-lg shadow-md overflow-hidden">
                                <div className="bg-blue-400 p-4">
                                    <h2 className="text-white text-lg font-bold">{classroom.title}</h2>
                                    <p className="text-white text-opacity-90">{classroom.decription}</p>
                                </div>
                                <div className="p-4 border-t border-zinc-200 dark:border-zinc-600">
                                    <div className="flex items-center mb-4">
                                        <img className="w-16 h-16 rounded-full" src="https://placehold.co/64x64" alt="Teacher Image" />
                                        <div className="ml-3">
                                            <h3 className="font-semibold dark:text-zinc-300">{classroom.description}</h3>
                                        </div>
                                    </div>
                                    <div className="flex justify-between items-center">
                                        
                                        <button className="text-blue-600 focus:outline-none dark:text-blue-400">
                                            Enroll
                                        </button>
                                    </div>
                                </div>
                            </div>

                        ))
                    ) : (
                        <p style={{ color: 'red', fontStyle: 'italic', textAlign: 'center' }}>No classrooms available</p>

                    )
                    }



                </div>
            </div>
        </>
    )
}

export default ClassroomCards
