// QuizGenerator.js

import React, { useState } from 'react';
import axios from 'axios';

function QuizGenerator() {
    const [inputText, setInputText] = useState('');
    const [questions, setQuestions] = useState([]);

    const handleCreateQuiz = async () => {
        try {
            const response = await axios.post('http://localhost:5000/generate_questions', { input_text: inputText });
            setQuestions(response.data.questions.map(question => ({ ...question, selectedOption: '', isCorrect: null })));
        } catch (error) {
            console.error('Error generating quiz:', error);
        }
    };

    const handleInputChange = (event) => {
        setInputText(event.target.value);
    };

    const handleOptionChange = (questionIndex, option) => {
        const updatedQuestions = [...questions];
        updatedQuestions[questionIndex].selectedOption = option;
        setQuestions(updatedQuestions);
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Quiz Generator</h1>
            <div className="mb-3">
                <textarea className="form-control" rows="4" value={inputText} onChange={handleInputChange}></textarea>
            </div>
            <button className="btn btn-primary" onClick={handleCreateQuiz}>Create Quiz</button>
            <div className="mt-4 d-flex flex-column align-items-center">
                {questions.map((question, index) => (
                    <div key={index} className="card w-75 mt-4">
                        <div className="card-body">
                            <h3 className="card-title">{question.question_statement}</h3>
                            <ul className="list-unstyled">
                                {question.options.map((option, optionIndex) => (
                                    <li
                                        key={optionIndex}
                                        className="option"
                                        onClick={() => handleOptionChange(index, option)}

                                        style={{
                                            color: question.isCorrect !== null ? (
                                                option === question.answer ? 'green' : (option === question.selectedOption ? 'red' : 'black')
                                            ) : 'black',
                                            cursor: question.isCorrect !== null ? 'not-allowed' : 'pointer',
                                            backgroundColor: question.isCorrect !== null && option === question.answer ? '#e6ffe6' : '',
                                            padding: '5px',
                                            marginBottom: '5px'
                                        }}
                                    >
                                        <input
                                            type="radio"
                                            id={`question-${index}-option-${optionIndex}`}
                                            name={`question-${index}`}
                                            value={option}
                                            checked={option === question.selectedOption}
                                            readOnly
                                        />
                                        <label htmlFor={`question-${index}-option-${optionIndex}`}>{option}</label>
                                    </li>
                                ))}
                            </ul>
                            {question.isCorrect !== null && (
                                <p className={`text-${question.isCorrect ? 'success' : 'danger'} mt-2`}>
                                    {question.isCorrect ? 'Correct Answer: ' : 'Wrong Answer: '} {question.answer}
                                </p>
                            )}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default QuizGenerator;
